#ifndef FW_CRC_H
#define FW_CRC_H

//--------------------------------------------------------------------------------------------------
//  INCLUDES
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
//  SWITCHES
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
//  MACRO DEFINITIONS
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
//  DECLARATIONS
//--------------------------------------------------------------------------------------------------

u16 FwCrc16(const void* data, size_t length);
u32 FwCrc32(const void* data, size_t length);
u32	FwCrc32String(const char* data);

//--------------------------------------------------------------------------------------------------
//  CLASS DEFINITIONS
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
//  INLINE FUNCTION DEFINITIONS
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
/**
	@brief			creates an 8-bit hash for some data

	@param			data			-	data pointer
	@param			length			-	length of data in bytes

	@return			the hash
**/
//--------------------------------------------------------------------------------------------------

inline u8 FwHash8(const void* data, size_t length)
{
	u16	h = FwCrc16(data, length);
	return u8(h ^ (h >> 8));
}

//--------------------------------------------------------------------------------------------------
/**
	@brief			creates a 16-bit hash for some data

	@param			data			-	data pointer
	@param			length			-	length of data in bytes

	@return			the hash
**/
//--------------------------------------------------------------------------------------------------

inline u16 FwHash16(const void* data, size_t length)
{
	return FwCrc16(data, length);
}

//--------------------------------------------------------------------------------------------------
/**
	@brief			creates a 32-bit hash for some data

	@param			data			-	data pointer
	@param			length			-	length of data in bytes

	@return			the hash
**/
//--------------------------------------------------------------------------------------------------

inline u32 FwHash32(const void* data, size_t length)
{
	return FwCrc32(data, length);
}

//--------------------------------------------------------------------------------------------------

#endif // FW_CRC_H
