#ifndef FP_RAY_H
#define FP_RAY_H

//--------------------------------------------------------------------------------------------------
//  INCLUDES
//--------------------------------------------------------------------------------------------------

#include	<Fw/FwMaths/FwVector.h>

//--------------------------------------------------------------------------------------------------
//  SWITCHES
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
//  MACRO DEFINITIONS
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
//  DECLARATIONS
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
//  CLASS DEFINITIONS
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
/**
	@class			FpRay

	@brief			Class describing a ray i.e. a semi-infinite line
**/
//--------------------------------------------------------------------------------------------------

class FpRay
{
public:

	// constructors
	FpRay() {}
	FpRay( const FpRay& rhs );
	FpRay( FwPoint_arg s, FwVector_arg d );

	// setting methods
	FpRay&			operator = ( const FpRay& rhs );
	void			Set( FwPoint_arg s, FwVector_arg d );
	void			SetStart( FwPoint_arg s );
	void			SetDirection( FwVector_arg d );

	// access
	const FwPoint&	GetStart() const;
	const FwVector&	GetDirection() const;

protected:

	FwPoint			m_start;							//!< the ray's start point
	FwVector		m_direction;						//!< the ray's direction

};

//--------------------------------------------------------------------------------------------------
//  INLINE FUNCTION DEFINITIONS
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
/**
	@brief			FpRay constructor

	@param			rhs				-	
**/
//--------------------------------------------------------------------------------------------------

inline FpRay::FpRay( const FpRay& rhs )
: m_start( rhs.m_start )
, m_direction( rhs.m_direction )
{
}

//--------------------------------------------------------------------------------------------------
/**
	@brief			FpRay constructor

	@param			s				-	
	@param			d				-	
**/
//--------------------------------------------------------------------------------------------------

inline FpRay::FpRay( FwPoint_arg s, FwVector_arg d )
: m_start( s )
, m_direction( d )
{
}

//--------------------------------------------------------------------------------------------------
/**
	@brief			

	@param			rhs				-	

	@return			
**/
//--------------------------------------------------------------------------------------------------

inline FpRay&	FpRay::operator = ( const FpRay& rhs )
{
	m_start = rhs.m_start;
	m_direction = rhs.m_direction;
	return *this;
}

//--------------------------------------------------------------------------------------------------
/**
	@brief			

	@param			s				-	
	@param			d				-	
**/
//--------------------------------------------------------------------------------------------------

inline void	FpRay::Set( FwPoint_arg s, FwVector_arg d )
{
	m_start = s;
	m_direction = d;
}

//--------------------------------------------------------------------------------------------------
/**
	@brief			

	@param			s				-	
**/
//--------------------------------------------------------------------------------------------------

inline void	FpRay::SetStart( FwPoint_arg s )
{
	m_start = s;
}

//--------------------------------------------------------------------------------------------------
/**
	@brief			

	@param			d				-	
**/
//--------------------------------------------------------------------------------------------------

inline void	FpRay::SetDirection( FwVector_arg d )
{
	m_direction = d;
}

//--------------------------------------------------------------------------------------------------
/**
	@brief			

	@return			
**/
//--------------------------------------------------------------------------------------------------

inline const FwPoint&	FpRay::GetStart() const
{
	return m_start;
}

//--------------------------------------------------------------------------------------------------
/**
	@brief			

	@return			
**/
//--------------------------------------------------------------------------------------------------

inline const FwVector&	FpRay::GetDirection() const
{
	return m_direction;
}

#endif // FP_RAY_H
