#ifndef FW_MATHS_HELPERS_H
#define FW_MATHS_HELPERS_H

//--------------------------------------------------------------------------------------------------
//	INCLUDES
//--------------------------------------------------------------------------------------------------

#include <Fw/FwMaths/FwMathsConstants.h>

//--------------------------------------------------------------------------------------------------
//	INLINE FUNCTION DEFINITIONS
//--------------------------------------------------------------------------------------------------

//--------------------------------------------------------------------------------------------------
/**
	@brief			Converts angle in degrees to radians.

	@param			angle		Angle in degrees.

	@return			Angle in radians.
**/
//--------------------------------------------------------------------------------------------------

inline float FwDegToRad(float angle) { return angle * FwMaths::kDegToRad; }

//--------------------------------------------------------------------------------------------------
/**
	@brief			Converts angle in radians to degrees.

	@param			angle		Angle in radians.

	@return			Angle in degrees.
**/
//--------------------------------------------------------------------------------------------------

inline float FwRadToDeg(float angle) { return angle * FwMaths::kRadToDeg; }

//--------------------------------------------------------------------------------------------------

#endif // FW_MATHS_HELPERS_H

